    public void ConsultarEgreso(JComboBox<String> egresos, String tituloEgreso) {
        try {
            String[] datos = new String[2];
            String texto;
            Connection cn1 = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst1 = cn1.prepareStatement("SELECT id, titulo FROM egresos WHERE titulo LIKE ?  ORDER BY titulo");
            pst1.setString(1, "%" + tituloEgreso + "%");

            ResultSet rs1 = pst1.executeQuery();

            while (rs1.next()) {
                datos[0] = rs1.getString(1);
                datos[1] = rs1.getString(2);
                texto = datos[0] + " - " + datos[1];
                egresos.addItem(texto);
            }
        } catch (SQLException e) {
            System.err.print(e);
        }
    }

