    private void BTN_AgregarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTN_AgregarMouseClicked
        try {

            String egresoSeleccionado = (String) id.getSelectedItem();
            String[] datosEgreso = egresoSeleccionado.split(" - ");
            String ID = datosEgreso[0];

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst = cn.prepareStatement("select * from egresos where id = ?");
            pst.setString(1, ID);

            ResultSet rs = pst.executeQuery();

            if (rs.next()) {
                id.setEnabled(false);
                buscarTitulo.setVisible(false);
                buscar_porTitulotxt.setVisible(false);
                id.setEditable(false);
                titulo.setVisible(true);
                fecha.setVisible(true);
                descripcion.setVisible(true);
                provedor.setVisible(true);
                monto.setVisible(true);

                titulo_txt.setVisible(true);
                fecha_txt.setVisible(true);
                descripcion_txt.setVisible(true);
                provedor_txt.setVisible(true);
                monto_txt.setVisible(true);
                BTN_Agregar.setVisible(false);
                BTN.setVisible(true);
                BTN_Actualizar.setVisible(true);
                BTN_Eliminar.setVisible(true);

                titulo.setText(rs.getString("titulo"));
                ((JTextField) fecha.getDateEditor().getUiComponent()).setText(rs.getString("fecha"));
                descripcion.setText(rs.getString("descripcion"));
                provedor.setText(rs.getString("provedor"));
                monto.setText(rs.getString("monto"));
            } else {
                JOptionPane.showMessageDialog(null, "Egreso no encontrado", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
                this.setVisible(false);
                new BuscarPK_Egreso().setVisible(true);
            }
        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTN_AgregarMouseClicked

