    private void BTN_ActualizarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTN_ActualizarMouseClicked
        try {

            String egresoSeleccionado = (String) id.getSelectedItem();
            String[] datosEgreso = egresoSeleccionado.split(" - ");
            String ID = datosEgreso[0];

            String TITULO = titulo.getText().trim();
            String FECHA = ((JTextField) fecha.getDateEditor().getUiComponent()).getText();
            String DESCRIPCION = descripcion.getText().trim();
            String PROVEDOR = provedor.getText().trim();
            String MONTO = monto.getText().trim();

            if (descripcion.equals("")) {
                DESCRIPCION = null;
            }

            if (provedor.equals("")) {
                PROVEDOR = null;
            }

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            String sql = "update egresos set titulo=?, fecha=?, descripcion=?, provedor=?, monto=? where id=?";
            PreparedStatement pst = cn.prepareStatement(sql);

            pst.setString(1, TITULO);
            pst.setString(2, FECHA);
            pst.setString(3, DESCRIPCION);
            pst.setString(4, PROVEDOR);
            pst.setString(5, MONTO);
            pst.setString(6, ID);

            pst.executeUpdate();
            JOptionPane.showMessageDialog(null, "Modificacion exitosa");
            this.setVisible(false);
            new BuscarPK_Egreso().setVisible(true);
        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTN_ActualizarMouseClicked

