            observacionesTable.setWidthPercentage(100);

// Agregar el encabezado de la tabla
            PdfPCell cell5 = new PdfPCell(new Phrase("Observaciones:", new Font(Font.FontFamily.HELVETICA, 12, Font.BOLD)));
            cell5.setHorizontalAlignment(Element.ALIGN_LEFT);
            cell5.setBorder(Rectangle.NO_BORDER);
            cell5.setPadding(5);
            observacionesTable.addCell(cell5);

// Agregar los datos de la tabla (esto es un ejemplo)
            PdfPCell cell6 = new PdfPCell(new Phrase(observaciones.getText(), new Font(Font.FontFamily.HELVETICA, 12, Font.NORMAL)));
            cell6.setHorizontalAlignment(Element.ALIGN_LEFT);
            cell6.setBorder(Rectangle.NO_BORDER);
            cell6.setPadding(5);
            observacionesTable.addCell(cell6);
// Agregar la tabla al documento
            documento.add(observacionesTable);
            documento.add(new Paragraph("\n"));

// Crear la nueva tabla con 5 columnas
            PdfPTable nuevaTabla = new PdfPTable(5);
            nuevaTabla.setWidthPercentage(100);

            PdfPCell cell7 = new PdfPCell(new Phrase("Datos de lo Realizado", new Font(Font.FontFamily.HELVETICA, 12, Font.BOLD)));
            cell7.setHorizontalAlignment(Element.ALIGN_CENTER);
            cell7.setColspan(5);
            nuevaTabla.addCell(cell7);

// Agregar los encabezados de las columnas
            nuevaTabla.addCell("Cod:");
