            documento.add(nuevaTabla);
// Agregar la nueva tabla
            documento.add(new Paragraph("\n")); // Salto de página
            PdfPTable totalsTable = new PdfPTable(2);
            totalsTable.setWidthPercentage(50);
            totalsTable.setSpacingBefore(20);
            totalsTable.setSpacingAfter(20);
            totalsTable.setHorizontalAlignment(Element.ALIGN_RIGHT);

// Agregar los datos de la tabla de totales
            totalsTable.addCell(new PdfPCell(new Phrase("Suma Neto:", new Font(Font.FontFamily.HELVETICA, 12, Font.BOLD))));
            totalsTable.addCell(new PdfPCell(new Phrase(suma.getText(), new Font(Font.FontFamily.HELVETICA, 12, Font.NORMAL))));
            totalsTable.addCell(new PdfPCell(new Phrase("Total IVA:", new Font(Font.FontFamily.HELVETICA, 12, Font.BOLD))));
            totalsTable.addCell(new PdfPCell(new Phrase(iva.getText(), new Font(Font.FontFamily.HELVETICA, 12, Font.NORMAL))));
            totalsTable.addCell(new PdfPCell(new Phrase("Total:", new Font(Font.FontFamily.HELVETICA, 12, Font.BOLD))));
            totalsTable.addCell(new PdfPCell(new Phrase(total.getText(), new Font(Font.FontFamily.HELVETICA, 12, Font.NORMAL))));

