    private void BTN_AgregarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTN_AgregarMouseClicked
        try {

            String clienteSeleccionado = (String) id.getSelectedItem();
            String[] datosCliente = clienteSeleccionado.split(" - ");
            String ID = datosCliente[0];

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst = cn.prepareStatement("select * from hoja_refacciones where id_refaccion = ?");
            pst.setString(1, ID);

            ResultSet rs = pst.executeQuery();

            if (rs.next()) {
                id.setEnabled(false);
                buscarRefaccion.setVisible(false);
                buscar_refacciontxt.setVisible(false);
                nombre.setVisible(true);
                precio.setVisible(true);

                nombre.setEditable(false);
                precio.setEditable(false);

                nombre_txt.setVisible(true);
                precio_txt.setVisible(true);

                BTN_Agregar.setVisible(false);
                BTN.setVisible(true);

                //id.setText(rs.getString("id_refaccion"));
                nombre.setText(rs.getString("nombre"));
                precio.setText(rs.getString("precio"));

            } else {
                JOptionPane.showMessageDialog(null, "Refaccion no encontrada", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
                this.setVisible(false);
                new EliminarRefaccion().setVisible(true);
            }
        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTN_AgregarMouseClicked

