    private void BTNMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTNMouseClicked
        try {

            String clienteSeleccionado = (String) id.getSelectedItem();
            String[] datosCliente = clienteSeleccionado.split(" - ");
            String ID = datosCliente[0];
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst = cn.prepareStatement("delete from hoja_refacciones where id_refaccion = ?");

            pst.setString(1, ID);
            pst.executeUpdate();

            JOptionPane.showMessageDialog(null, "Refaccion eliminada", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
            this.setVisible(false);
            new EliminarRefaccion().setVisible(true);

        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTNMouseClicked

