    private void forma_pagoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_forma_pagoKeyTyped
        char c = evt.getKeyChar();
        if ((c < 'a' || c > 'z') && (c < 'A' || c > 'Z')
                && (c != (char) KeyEvent.VK_BACKSPACE) && (c != (char) KeyEvent.VK_SPACE)) {
            evt.consume();
            JOptionPane.showMessageDialog(this, "Solo se permiten letras.", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
        } else {

        }
        // TODO add your handling code here:
    }//GEN-LAST:event_forma_pagoKeyTyped

    private void totalKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_totalKeyTyped
        char c = evt.getKeyChar();
        if ((c < '0' || c > '9') && (c != (char) KeyEvent.VK_BACKSPACE)) {
            evt.consume();
            JOptionPane.showMessageDialog(this, "Solo se permiten números.", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
        } else {

        }
        // TODO add your handling code here:
    }//GEN-LAST:event_totalKeyTyped

    private void BTN_Agregar1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTN_Agregar1MouseClicked
        try {

            String IDSeleccionado = (String) idd.getSelectedItem();
            String[] datosPRE = IDSeleccionado.split(" - ");
            String IDD = datosPRE[0];

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst = cn.prepareStatement("select * from presupuesto1 where id_presupuesto = ?");
            pst.setString(1, IDD);

            ResultSet rs = pst.executeQuery();

            if (rs.next()) {

                fecha_presupuesto.setVisible(true);
                id_cliente2.setVisible(true);
                forma_pago.setVisible(true);
                ID_Entrada.setVisible(true);
                total.setVisible(true);
                observaciones.setVisible(true);

                fecha_txt.setVisible(true);
                id_cliente_txt.setVisible(true);
                forma_pago_txt.setVisible(true);
                id_entrada_txt.setVisible(true);
                total_txt.setVisible(true);
                observaciones_txt.setVisible(true);

                BTN_Agregar1.setVisible(false);
                BTN.setVisible(true);

                ((JTextField) fecha_presupuesto.getDateEditor().getUiComponent()).setText(rs.getString("fecha"));
                forma_pago.setText(rs.getString("forma_pago"));
                observaciones.setText(rs.getString("observaciones"));
                total.setText(rs.getString("total"));

                // Obtener cliente
                String rfcCliente = rs.getString("presupuesto1.id_cliente");
                // Buscar el nombre del cliente asociado a ese RFC
                PreparedStatement pstCliente = cn.prepareStatement("SELECT nombre FROM clientes WHERE rfc = ?");
                pstCliente.setString(1, rfcCliente);
                ResultSet rsCliente = pstCliente.executeQuery();

                if (rsCliente.next()) {
                    String nombreCliente = rsCliente.getString("nombre");
                    id_cliente2.setSelectedItem(rfcCliente + " - " + nombreCliente);
                }

                // Obtener entrada
                String entrada = rs.getString("presupuesto1.placas");
                // Buscar el nombre del cliente asociado a ese RFC
                PreparedStatement pstENT = cn.prepareStatement("SELECT placas FROM entrada WHERE id = ?");
                pstENT.setString(1, entrada);
                ResultSet rsENT = pstENT.executeQuery();

                if (rsENT.next()) {
                    String PLACASS = rsENT.getString("placas");
                    ID_Entrada.setSelectedItem(entrada + " - " + PLACASS);
                }

            } else {
                JOptionPane.showMessageDialog(null, "Presupuesto no encontrado", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
                this.setVisible(false);
                new ModificarDatosPresupuesto().setVisible(true);
            }
        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTN_Agregar1MouseClicked

    private void BTNMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTNMouseClicked
        try {
            String entradaSeleccionada = (String) ID_Entrada.getSelectedItem();
            String[] datosCliente = entradaSeleccionada.split(" - ");
            String ENT = datosCliente[0];

            String IDSeleccionada = (String) idd.getSelectedItem();
            String[] datosID = IDSeleccionada.split(" - ");
            String IDD = datosID[0];

            String cliSeleccionadO = (String) id_cliente2.getSelectedItem();
            String[] datosCLI = cliSeleccionadO.split(" - ");
            String CLI = datosCLI[0];

            String Fecha = ((JTextField) fecha_presupuesto.getDateEditor().getUiComponent()).getText();
            String Observaciones = observaciones.getText().trim();
            String Forma_pago = forma_pago.getText().trim();
            String Total = total.getText().trim();

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst = cn.prepareStatement("update presupuesto1 set id_presupuesto = ?, fecha = ?, id_cliente = ?, forma_pago = ?, observaciones = ?, placas = ?, total = ? where id_presupuesto = ?");
            pst.setString(1, IDD);
            pst.setString(2, Fecha);
            pst.setString(3, CLI);
            pst.setString(4, Forma_pago);
            pst.setString(5, Observaciones);
            pst.setString(6, ENT);
            pst.setString(7, Total);
            pst.setString(8, IDD);

            pst.executeUpdate();
            JOptionPane.showMessageDialog(null, "Modificacion exitosa");
            this.setVisible(false);
            new ModificarDatosPresupuesto().setVisible(true);

        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTNMouseClicked

