    public void ConsultarCliente(JComboBox Clientes) {
        try {

            String[] datos = new String[5];
            String texto;
            Connection cn1 = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst1 = cn1.prepareStatement("SELECT presupuesto.idd, presupuesto.id_presupuesto,clientes.nombre, hoja_refacciones.nombre,presupuesto.Cantidad FROM presupuesto,hoja_refacciones,clientes,presupuesto1 WHERE hoja_refacciones.id_refaccion= presupuesto.refaccion && presupuesto1.id_presupuesto = presupuesto.id_presupuesto && presupuesto1.id_cliente = clientes.rfc ORDER BY presupuesto.idd DESC");

            ResultSet rs1 = pst1.executeQuery();

            while (rs1.next()) {
                datos[0] = rs1.getString(1);
                datos[1] = rs1.getString(2);
                datos[2] = rs1.getString(3);
                datos[3] = rs1.getString(4);
                datos[4] = rs1.getString(5);
                texto = datos[0] + "                                     " + datos[1] + "                               " + datos[2] + "          " + datos[3] + "        " + datos[4];
                Clientes.addItem(texto);

            }

        } catch (SQLException e) {
            System.err.print(e);
        }
    }

