    private void BTNMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BTNMouseClicked
        try {

            String IDSeleccionada = (String) ID.getSelectedItem();
            String[] datosCliente = IDSeleccionada.split(" - ");
            String ID = datosCliente[0];

            String CANTIDAD = cantidad.getText().trim();
            double cantidadDouble = Double.parseDouble(CANTIDAD);
            String REFACCION = refaccion.getText().trim();
            String PRESUPUESTO = presupuesto.getText().trim();

            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/tecnoauto", "root", "");
            PreparedStatement pst = cn.prepareStatement("delete from presupuesto where idd = ?");

            pst.setString(1, ID);
            pst.executeUpdate();
            //Valor actual
            PreparedStatement pst1 = cn.prepareStatement("SELECT total FROM presupuesto1 WHERE id_presupuesto = ?");
            pst1.setString(1, PRESUPUESTO);
            ResultSet rs1 = pst1.executeQuery();
            double totalActual = 0.0;

            if (rs1.next()) {
                totalActual = rs1.getDouble("total");
                System.out.println("Actual:" + totalActual);

                // Calcular el resultado de precio refaccion * cantidad
                PreparedStatement pst3 = cn.prepareStatement("SELECT precio FROM hoja_refacciones WHERE id_refaccion = ?");
                pst3.setString(1, REFACCION);
                ResultSet rs = pst3.executeQuery();

                if (rs.next()) {
                    double precioRefaccion = rs.getDouble("precio");
                    System.out.println("Precio de la refacción: " + precioRefaccion);

                    double resultadoMultiplicacion = (precioRefaccion * cantidadDouble) * 1.16;
                    System.out.println("Multiplicación: " + resultadoMultiplicacion);

                    // Restar el resultado al total actual
                    double nuevoTotal = totalActual - resultadoMultiplicacion;

                    // Actualizar el total en la base de datos
                    PreparedStatement pst2 = cn.prepareStatement("UPDATE presupuesto1 SET total = ? WHERE id_presupuesto = ?");
                    pst2.setDouble(1, nuevoTotal);
                    pst2.setString(2, PRESUPUESTO);
                    pst2.executeUpdate();
                } else {
                    System.out.println("No esta entrando");
                }
            }

            JOptionPane.showMessageDialog(null, "Se elimino la refaccion o mano de obra correctamente", "Mi Taller", JOptionPane.INFORMATION_MESSAGE);
            this.setVisible(false);
            new QuitarCosasPresupuesto().setVisible(true);
        } catch (Exception e) {
            System.err.print(e);
        }
        // TODO add your handling code here:
    }//GEN-LAST:event_BTNMouseClicked

